import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './Contact.css';

const Contact = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState('');

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Ïã§Ï†ú Ìèº Ï†úÏ∂ú Î°úÏßÅÏùÑ Ïó¨Í∏∞Ïóê Íµ¨ÌòÑ
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitStatus('success');
      setFormData({ name: '', email: '', subject: '', message: '' });

      setTimeout(() => {
        setSubmitStatus('');
      }, 3000);
    }, 1000);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  const contactInfo = [
    {
      icon: 'üìß',
      title: 'Ïù¥Î©îÏùº',
      value: 'your.email@example.com',
      link: 'mailto:your.email@example.com'
    },
    {
      icon: 'üì±',
      title: 'Ï†ÑÌôîÎ≤àÌò∏',
      value: '+82 10-1234-5678',
      link: 'tel:+821012345678'
    },
    {
      icon: 'üìç',
      title: 'ÏúÑÏπò',
      value: 'ÏÑúÏö∏, ÎåÄÌïúÎØºÍµ≠',
      link: '#'
    },
    {
      icon: 'üíº',
      title: 'LinkedIn',
      value: 'linkedin.com/in/yourprofile',
      link: 'https://linkedin.com/in/yourprofile'
    }
  ];

  return (
    <section id="contact" className="section contact">
      <motion.div
        ref={ref}
        className="contact-container"
        variants={containerVariants}
        initial="hidden"
        animate={inView ? "visible" : "hidden"}
      >
        <motion.h2 className="section-title" variants={itemVariants}>
          Ïó∞ÎùΩÌïòÍ∏∞
        </motion.h2>

        <motion.div className="contact-intro" variants={itemVariants}>
          <p>
            ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ÎÇò ÌòëÏóÖ Í∏∞ÌöåÏóê ÎåÄÌï¥ Ïù¥ÏïºÍ∏∞ÌïòÍ≥† Ïã∂ÏúºÏãúÎã§Î©¥ Ïñ∏Ï†úÎì† Ïó∞ÎùΩÏ£ºÏÑ∏Ïöî. 
            <span className="blue-accent"> 24ÏãúÍ∞Ñ ÎÇ¥</span>Ïóê ÎãµÎ≥ÄÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.
          </p>
        </motion.div>

        <div className="contact-content">
          <motion.div className="contact-info" variants={itemVariants}>
            <h3>Ïó∞ÎùΩ Ï†ïÎ≥¥</h3>
            <div className="contact-items">
              {contactInfo.map((item, index) => (
                <motion.a
                  key={index}
                  href={item.link}
                  className="contact-item card"
                  whileHover={{ scale: 1.05, y: -5 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <div className="contact-icon">{item.icon}</div>
                  <div className="contact-details">
                    <h4>{item.title}</h4>
                    <p>{item.value}</p>
                  </div>
                </motion.a>
              ))}
            </div>

            <motion.div
              className="social-links"
              variants={itemVariants}
            >
              <h4>ÏÜåÏÖú ÎØ∏ÎîîÏñ¥</h4>
              <div className="social-icons">
                <motion.a
                  href="#"
                  className="social-icon"
                  whileHover={{ scale: 1.2, rotate: 5 }}
                  whileTap={{ scale: 0.9 }}
                >
                  GitHub
                </motion.a>
                <motion.a
                  href="#"
                  className="social-icon"
                  whileHover={{ scale: 1.2, rotate: -5 }}
                  whileTap={{ scale: 0.9 }}
                >
                  LinkedIn
                </motion.a>
                <motion.a
                  href="#"
                  className="social-icon"
                  whileHover={{ scale: 1.2, rotate: 5 }}
                  whileTap={{ scale: 0.9 }}
                >
                  Twitter
                </motion.a>
              </div>
            </motion.div>
          </motion.div>

          <motion.div className="contact-form-container" variants={itemVariants}>
            <div className="card">
              <h3>Î©îÏãúÏßÄ Î≥¥ÎÇ¥Í∏∞</h3>

              {submitStatus === 'success' && (
                <motion.div
                  className="success-message"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                >
                  Î©îÏãúÏßÄÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÑÏÜ°ÎêòÏóàÏäµÎãàÎã§! Í≥ß Ïó∞ÎùΩÎìúÎ¶¨Í≤†ÏäµÎãàÎã§.
                </motion.div>
              )}

              <form className="contact-form" onSubmit={handleSubmit}>
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="name">Ïù¥Î¶Ñ *</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      required
                      placeholder="ÌôçÍ∏∏Îèô"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="email">Ïù¥Î©îÏùº *</label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      required
                      placeholder="example@email.com"
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="subject">Ï†úÎ™© *</label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleInputChange}
                    required
                    placeholder="ÌîÑÎ°úÏ†ùÌä∏ Î¨∏Ïùò"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="message">Î©îÏãúÏßÄ *</label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                    required
                    rows="5"
                    placeholder="ÌîÑÎ°úÏ†ùÌä∏Ïóê ÎåÄÌïú ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©ÏùÑ ÏïåÎ†§Ï£ºÏÑ∏Ïöî..."
                  ></textarea>
                </div>

                <motion.button
                  type="submit"
                  className={`btn submit-btn ${isSubmitting ? 'submitting' : ''}`}
                  disabled={isSubmitting}
                  whileHover={{ scale: isSubmitting ? 1 : 1.05 }}
                  whileTap={{ scale: isSubmitting ? 1 : 0.95 }}
                >
                  {isSubmitting ? 'Ï†ÑÏÜ° Ï§ë...' : 'Î©îÏãúÏßÄ Î≥¥ÎÇ¥Í∏∞'}
                </motion.button>
              </form>
            </div>
          </motion.div>
        </div>
      </motion.div>
    </section>
  );
};

export default Contact;
